.columns {
  margin-left: (-$column-gap);
  margin-right: (-$column-gap);
  margin-top: (-$column-gap);
  &:last-child {
    margin-bottom: (-$column-gap); }
  &:not(:last-child) {
    margin-bottom: calc(1.5rem - #{$column-gap}); }
  // Modifiers
  &.is-centered {
    justify-content: center; }
  &.is-gapless {
    margin-left: 0;
    margin-right: 0;
    margin-top: 0;
    & > .column {
      margin: 0;
      padding: 0 !important; }
    &:not(:last-child) {
      margin-bottom: 1.5rem; }
    &:last-child {
      margin-bottom: 0; } }
  &.is-mobile {
    display: flex; }
  &.is-multiline {
    flex-wrap: wrap; }
  &.is-vcentered {
    align-items: center; }
  // Responsiveness
  @include tablet {
    &:not(.is-desktop) {
      display: flex; } }
  @include desktop {
    // Modifiers
    &.is-desktop {
      display: flex; } } }

@if $variable-columns {
  .columns.is-variable {
    --columnGap: 0.75rem;
    margin-left: calc(-1 * var(--columnGap));
    margin-right: calc(-1 * var(--columnGap));
    .column {
      padding-left: var(--columnGap);
      padding-right: var(--columnGap); }
    @for $i from 0 through 8 {
      &.is-#{$i} {
        --columnGap: #{$i * 0.25rem}; }
      @include mobile {
        &.is-#{$i}-mobile {
          --columnGap: #{$i * 0.25rem}; } }
      @include tablet {
        &.is-#{$i}-tablet {
          --columnGap: #{$i * 0.25rem}; } }
      @include tablet-only {
        &.is-#{$i}-tablet-only {
          --columnGap: #{$i * 0.25rem}; } }
      @include touch {
        &.is-#{$i}-touch {
          --columnGap: #{$i * 0.25rem}; } }
      @include desktop {
        &.is-#{$i}-desktop {
          --columnGap: #{$i * 0.25rem}; } }
      @include desktop-only {
        &.is-#{$i}-desktop-only {
          --columnGap: #{$i * 0.25rem}; } }
      @include widescreen {
        &.is-#{$i}-widescreen {
          --columnGap: #{$i * 0.25rem}; } }
      @include widescreen-only {
        &.is-#{$i}-widescreen-only {
          --columnGap: #{$i * 0.25rem}; } }
      @include fullhd {
        &.is-#{$i}-fullhd {
          --columnGap: #{$i * 0.25rem}; } } } } }
","$container-offset: (2 * $gap) !default;

.container {
  flex-grow: 1;
  margin: 0 auto;
  position: relative;
  width: auto;
  @include desktop {
    max-width: $desktop - $container-offset;
    &.is-fluid {
      margin-left: $gap;
      margin-right: $gap;
      max-width: none; } }
  @include until-widescreen {
    &.is-widescreen {
      max-width: $widescreen - $container-offset; } }
  @include until-fullhd {
    &.is-fullhd {
      max-width: $fullhd - $container-offset; } }
  @include widescreen {
    max-width: $widescreen - $container-offset; }
  @include fullhd {
    max-width: $fullhd - $container-offset; } }
