      line-height: $s4-lh;
      padding: $baseline 0 !important;
    }
  }

  .btn--accordion {
    &::after {
      content: "\e904";
      font-family: "icomoon", Arial, sans-serif;
      color: $secondary;
      font-size: $s4-text;
      margin-left: $baseline;
      margin-top: 0.2rem;
      display: block;

      @include mq($until: mobile) {
        margin-left: 0;
      }
    }
  }

